mapper.select:
SELECT id, user_id, name, email, birth, phone_number
FROM user
where id = #{id}

SELECT id, user_id, name, manager
FROM user
WHERE user_id = #{userId} AND password = #{password}

mapper.update:
UPDATE user
SET user_id = #{userId}, name = #{name},
	email = #{email}, birth = #{birth}, phone_number = #{phoneNumber}
WHERE id = #{id}
